package fr.unice.spinefm.ActionModel.SystemActionModel.impl;import org.apache.log4j.Logger;public class ActionSetProductDescriptionImplDelegate extends ActionSetProductDescriptionImpl {	private static Logger log = Logger.getLogger(ActionSetProductDescriptionImplDelegate.class);		@Override	public void apply() {		this.getProduct().setDescription(this.getNewName());	}		@Override	public void undo() {		this.getProduct().setDescription(this.getOldName());	}		public String toString() {		return "Action SetProductDescription ["+this.getOldName()+" -> "+this.getNewName()+" | Type : "+this.getType()+"] "+this.getStep();	}}