package fr.unice.spinefm.stats;import java.util.ArrayList;import java.util.List;import javax.xml.bind.annotation.XmlElement;import javax.xml.bind.annotation.XmlRootElement;@XmlRootElementpublic class PropagationStats {	private List<PropagationStep> propSteps;		public PropagationStats() {		this.propSteps = new ArrayList<PropagationStep>();	}		public void addPropStep(PropagationStep ps) {		this.propSteps.add(ps);	}		@XmlElement	public List<PropagationStep> getSteps() {		return this.propSteps;	}		public String toString() {		String result = "Date\tDuration\tFinished Config\tImpacted Contexts\tImpacted FMs\tNb links\tTotal Contexts\tSystem Actions\tIs Gobal Context Impacted?\tPath length\tPath\tEntry point\n";		for (PropagationStep ps : this.propSteps) {			result += ps.toString();		}		return result;	}}