package fr.unice.spinefm.stats;import java.util.Date;import java.util.List;import javax.xml.bind.annotation.XmlElement;import javax.xml.bind.annotation.XmlRootElement;@XmlRootElementpublic class PropagationStep {	private Date date;	private long duration;	private String domainElement;	private int nbSystemActions;	private int nbTotalCtx;	private int nbImpactedCtx;	private int nbFinishedConfig;	private int nbLinks;	private int nbImpactedFM;	private boolean isGlobalCtxImpacted;	private List<String> path;		public PropagationStep() {}	public PropagationStep(Date date, long duration, String domainElement,			int nbSystemActions, int nbTotalCtx,			int nbImpactedCtx, int nbFinishedConfig, int nbLinks,			int nbImpactedFM, boolean isGlobalCtxImpacted, List<String> path) {		super();		this.date = date;		this.duration = duration;		this.domainElement = domainElement;		this.nbSystemActions = nbSystemActions;		this.nbTotalCtx = nbTotalCtx;		this.nbImpactedCtx = nbImpactedCtx;		this.nbFinishedConfig = nbFinishedConfig;		this.nbLinks = nbLinks;		this.nbImpactedFM = nbImpactedFM;		this.isGlobalCtxImpacted = isGlobalCtxImpacted;		this.path = path;	}	@XmlElement	public Date getDate() {		return date;	}	public void setDate(Date date) {		this.date = date;	}	@XmlElement	public long getDuration() {		return duration;	}	public void setDuration(long duration) {		this.duration = duration;	}	@XmlElement	public String getDomainElement() {		return domainElement;	}	public void setDomainElement(String domainElement) {		this.domainElement = domainElement;	}	@XmlElement	public int getNbSystemActions() {		return nbSystemActions;	}	public void setNbSystemActions(int nbSystemActions) {		this.nbSystemActions = nbSystemActions;	}	@XmlElement	public int getNbTotalCtx() {		return nbTotalCtx;	}	public void setNbTotalCtx(int nbTotalCtx) {		this.nbTotalCtx = nbTotalCtx;	}	@XmlElement	public int getNbImpactedCtx() {		return nbImpactedCtx;	}	public void setNbImpactedCtx(int nbImpactedCtx) {		this.nbImpactedCtx = nbImpactedCtx;	}	@XmlElement	public int getNbFinishedConfig() {		return nbFinishedConfig;	}	public void setNbFinishedConfig(int nbFinishedConfig) {		this.nbFinishedConfig = nbFinishedConfig;	}	@XmlElement	public int getNbLinks() {		return nbLinks;	}	public void setNbLinks(int nbLinks) {		this.nbLinks = nbLinks;	}	@XmlElement	public int getNbImpactedFM() {		return nbImpactedFM;	}	public void setNbImpactedFM(int nbImpactedFM) {		this.nbImpactedFM = nbImpactedFM;	}	@XmlElement	public boolean isGlobalCtxImpacted() {		return isGlobalCtxImpacted;	}	public void setGlobalCtxImpacted(boolean isGlobalCtxImpacted) {		this.isGlobalCtxImpacted = isGlobalCtxImpacted;	}		@XmlElement	public List<String> getPath() {		return this.path;	}		@XmlElement	public int getPathLength() {		return this.path.size();	}		public String toString() {		return this.date.toLocaleString()+"\t"+this.duration+"\t"+this.nbFinishedConfig+"\t"+this.nbImpactedCtx+"\t"+this.nbImpactedFM+"\t"+this.nbLinks+"\t"+this.nbTotalCtx+"\t"+this.nbSystemActions+"\t"+this.isGlobalCtxImpacted+"\t"+this.path.size()+"\t"+this.path+"\t"+this.domainElement+"\n";	}}